<button wire:click="toggleUpvote()" type="button" class="btn {{(Auth::user()?->hasUpvoted($this->post)) ? ' focus:out bg-green-700 hover:bg-green-800 focus:ring-4 focus:ring-green-300 font-medium rounded-lg text-sm  py-2.5  mb-2 dark:bg-green-600 dark:hover:bg-green-700 dark:focus:ring-green-800' : 'text-gray-900 bg-white border border-gray-300 focus:outline-none hover:bg-gray-100 focus:ring-4 focus:ring-gray-100 font-medium rounded-lg text-sm  py-2.5  mb-2 dark:bg-gray-800 dark:border-gray-600 dark:hover:bg-gray-700 dark:hover:border-gray-600 dark:focus:ring-gray-700'}}" data-bs-toggle="button" aria-pressed="false">
    <i class="fa fa-arrow-up {{(Auth::user()?->hasUpvoted($this->post)) ? 'text-white' : 'text-green-700'}}me-2"></i>
    <span class="font-weight-bold">{{$this->post->likes()->count()}}</span>
</button>
